const Registration = () => {

    const [inputValue, setInputValue] = React.useState('');

    const addSetValue = (event) => {
        setInputValue(event.target.value);
    }

    return (
        <>
            <div className='main'>
                <div className='container'>
                    <div className='main__content'>

                        <p>введите ваше имя: {inputValue}</p>
                        <MyInput
                            type='text'
                            placeholder='ваше имя'
                            value={inputValue}
                            onChange={addSetValue}
                        />


                        <MyInput
                            type='text'
                            placeholder='ваше имя'
                            value={inputValue}
                            onChange={addSetValue}
                        />

                        <MyInput
                            type='text'
