import React from 'react';
import '../styles/RegistrationStyle.css';
import MyInput from "../components/MyInput";
import MyButton from "../components/MyButton";
import { useForm } from "react-hook-form";

const Registration = () => {
    const {
        register,
        handleSubmit,
        formState: { errors }
    } = useForm({
        defaultValues: {
            name: '',
            lastName: '',
            email: '',
            password: ''
        }
    });

    const onSubmit = data => console.log(data);

    return (
        <>
            <div className='main'>
                <div className='container'>
                    <div className='main__content'>
                        <h1>Регистрация</h1>

                        <form onSubmit={handleSubmit(onSubmit)}>
                            <p>Имя</p>
                            <MyInput {...register("name", { required: "" })} />
                            {errors.name && <p>{errors.name.message}</p>}

                            <p>Фамилия</p>
                            <MyInput {...register("lastName", { required: "Фамилия обязательна для заполнения" })} />
                            {errors.lastName && <p>{errors.lastName.message}</p>}

                            <p>Почта</p>
                            <MyInput {...register("email", { required: "Email обязателен для заполнения", maxLength: 20 })} />
                            {errors.email && <p>{errors.email.message}</p>}

                            <p>Пароль</p>
                            <MyInput {...register("password", { required: "Пароль обязателен для заполнения", maxLength: 20 })} />
                            {errors.password && <p>{errors.password.message}</p>}

                            <MyButton type="submit">Зарегистрироваться</MyButton>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Registration;
