import React from 'react';
import '../styles/RegistrationStyle.css';
import MyInput from "../components/MyInput";
import MyButton from "../components/MyButton";
import useFormValidation from "../templates/useFormValidation";

const Registration = () => {
    // Определяем начальное состояние и валидаторы
    const initialState = {
        inputName: '',
        inputLastName: '',
        inputMail: '',
        inputPass: ''
    };

    const validators = {
        inputName: (value) => {
            if (!value) return 'Имя обязательно для заполнения';
            return '';
        },
        inputLastName: (value) => {
            if (!value) return 'Фамилия обязательна для заполнения';
            return '';
        },
        inputMail: (value) => {
            if (!value) return 'Email обязателен для заполнения';
            // Добавьте дополнительные проверки для email, если необходимо
            return '';
        },
        inputPass: (value) => {
            if (!value) return 'Пароль обязателен для заполнения';
            // Добавьте дополнительные проверки для пароля, если необходимо
            return '';
        }
    };

    // Используем пользовательский хук
    const { values, errors, setValues, setErrors, handleChange, handleSubmit } = useFormValidation(initialState, validators);

    const onSubmit = () => {
        // Выполняем валидацию при отправке формы
        const validationErrors = {};
        Object.keys(validators).forEach(key => {
            const error = validators[key](values[key]);
            if (error) {
                validationErrors[key] = error;
            }
        });

        setErrors(validationErrors);

        if (Object.keys(validationErrors).length === 0) {
            // Если ошибок нет, обрабатываем отправку формы
            console.log('Форма отправлена:', values);
        }
    };

    return (
        <>
            <div className='main'>
                <div className='container'>
                    <div className='main__content'>
                        <h1>Регистрация</h1>

                        <form onSubmit={handleSubmit(onSubmit)}>
                            <p>Имя</p>
                            <MyInput
                                type='text'
                                name='inputName'
                                placeholder='ваше имя'
                                value={values.inputName}
                                onChange={handleChange}
                            />
                            {errors.inputName && <p>{errors.inputName}</p>}

                            <p>Фамилия</p>
                            <MyInput
                                type='text'
                                name='inputLastName'
                                placeholder='ваша фамилия'
                                value={values.inputLastName}
                                onChange={handleChange}
                            />
                            {errors.inputLastName && <p>{errors.inputLastName}</p>}

                            <p>Почта</p>
                            <MyInput
                                type='text'
                                name='inputMail'
                                placeholder='ваша почта'
                                value={values.inputMail}
                                onChange={handleChange}
                            />
                            {errors.inputMail && <p>{errors.inputMail}</p>}

                            <p>Пароль</p>
                            <MyInput
                                type='password'
                                name='inputPass'
                                placeholder='ваш пароль'
                                value={values.inputPass}
                                onChange={handleChange}
                            />
                            {errors.inputPass && <p>{errors.inputPass}</p>}

                            <MyButton type="submit">Зарегистрироваться</MyButton>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Registration;
