import React from 'react';
import './CreateProjectModal.css';
import MyButton from "../MyButton/MyButton";
import MyInput from "../MyInput/MyInput";
import CrossClose from "../../assets/svg/CrossClose";

const CreateProjectModal = ({ onClose }) => {
    const [status, setStatus] = React.useState('active');

    const handleCancel = () => {
        onClose();
    };

    const handleCreateProject = () => {
        // Логика создания проекта
        onClose();
    };

    return (
        <div className="create-project-modal">
            <div className="create-project__content">
                <div className="create-project__header">
                    <h2>Создание проекта</h2>
                </div>
                <form action="">
                    <div className="form__items">
                        <label>
                            Название проекта
                            <MyInput type="text" placeholder="Введите название проекта" />
                        </label>
                    </div>
                    <div className="form__items">
                        <label>
                            Описание проекта
                            <MyInput type="text" placeholder="Введите описание проекта" />
                        </label>
                    </div>
                    <div className="form__items">
                        <select value={status} onChange={(e) => setStatus(e.target.value)}>
                            <option value="active">Активный</option>
                            <option value="inactive">Неактивный</option>
                        </select>
                    </div>
                    <div className="form__items">
                        <label>
                            Пригласить в проект
                            <MyInput type="text" placeholder="Введите email" />
                        </label>
                    </div>
                    <div className="form__buttons">
                        <MyButton className="form__button" onClick={handleCancel}>Отменить</MyButton>
                        <MyButton className="form__button" onClick={handleCreateProject}>Создать проект</MyButton>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default CreateProjectModal;
