import React, { useState } from 'react';
import CalendarSvg from '../../assets/svg/SideBar/CalendarSvg.jsx';
import InvitationsSvg from '../../assets/svg/SideBar/InvitationsSvg.jsx';
import LogoutSvg from '../../assets/svg/SideBar/LogoutSvg.jsx';
import MainSvg from '../../assets/svg/SideBar/MainSvg.jsx';
import ParticipantsSvg from '../../assets/svg/SideBar/ParticipantsSvg.jsx';
import ProfileSvg from '../../assets/svg/SideBar/ProfileSvg.jsx';
import SideBar from '../../assets/svg/SideBar/SideBar.jsx';
import TasksSvg from '../../assets/svg/SideBar/TasksSvg.jsx';
import ProjectsSvg from "../../assets/svg/SideBar/ProjectsSvg";
import SettingSvg from "../../assets/svg/SideBar/SettingSvg";
import { Layout, Menu } from 'antd';
import './NavBarStyle.css'; // Импортируйте CSS файл

const { Sider } = Layout;

const Sidebar = () => {
    const [collapsed, setCollapsed] = useState(false);

    const toggleCollapsed = () => {
        setCollapsed(!collapsed);
    };

    return (
        // <div className="Side-bar">
        <Sider
            className="sidebar" // Примените класс sidebar
            collapsed={collapsed}
            breakpoint="lg"
            collapsedWidth="80" // Установите ширину свернутого состояния
            width={200} // Установите ширину развернутого состояния
            onBreakpoint={(broken) => {
                console.log(broken);
            }}
        >
            <div className="logo" />
            <Menu theme="light" mode="inline" defaultSelectedKeys={['1']}>
                <Menu.Item key="11" icon={<SideBar />} onClick={toggleCollapsed}>
                    {collapsed ? 'Развернуть' : 'Свернуть'}
                </Menu.Item>
                <div className="menu-divider"></div> {/* Разделитель */}
                <Menu.Item key="1" icon={<MainSvg />}>
                    Главная
                </Menu.Item>
                <Menu.Item key="2" icon={<TasksSvg />}>
                    Задачи
                </Menu.Item>
                <Menu.Item key="3" icon={<ProjectsSvg />}>
                    Проекты
                </Menu.Item>
                <Menu.Item key="4" icon={<InvitationsSvg />}>
                    Приглашения
                </Menu.Item>
                <Menu.Item key="5" icon={<CalendarSvg />}>
                    Календарь
                </Menu.Item>
                <Menu.Item key="6" icon={<ParticipantsSvg />}>
                    Участники
                </Menu.Item>
                <Menu.Item key="7" icon={<ProfileSvg />}>
                    Профиль
                </Menu.Item>
                <div className="menu-divider"></div> {/* Разделитель */}
                <Menu.Item key="8" icon={<SettingSvg />}>
                    Настройки
                </Menu.Item>
                <Menu.Item key="9" icon={<LogoutSvg />}>
                    Выход из системы
                </Menu.Item>
            </Menu>
        </Sider>
    );
};

export default Sidebar;
