import React, {Component} from 'react';
import '../styles/RegistrationStyle.css'
import MyInput from "../components/MyInput";
import MyButton from "../components/MyButton";
import useFormValidation from "../templates/useFormValidation";

const Registration = () => {

    const [inputName, setInputName] = React.useState('');
    const [inputLastName, setInputLastName] = React.useState('')
    const [inputMail, setInputMail] = React.useState('');
    const [inputPass, setInputPass] = React.useState('');

    const addSetName = (e) => {
        setInputName(e.target.value);
    }

    const addSetLastName = (e) => {
        setInputLastName(e.target.value);
    }

    const addSetMail = (e) => {
        setInputMail(e.target.value);
    }

    const addSetPass = (e) => {
        setInputPass(e.target.value);
    }

    const validators = {
        email: (value) => {
            if (!value) return 'Email is required';
            if (!/\S+@\S+\.\S+/.test(value)) return 'Invalid email address';
            return '';
        },
        password: (value) => {
            if (!value) return 'Password is required';
            if (value.length < 6) return 'Password must be at least 6 characters';
            return '';
        },
    };

        const { values, errors, handleChange, handleSubmit } = useFormValidation(
            { email: '', password: '' },
            validators
        );

        const submitForm = () => {
            console.log('Form submitted', values);
        };

    return (
        <>
            <div className='main'>
                <div className='container'>
                    <div className='main__content'>
                        <h1>Регистрация</h1>

                        <form onSubmit={handleSubmit(submitForm)}>

                            <p>Имя</p>
                            <MyInput
                                type='text'
                                placeholder='ваше имя'
                                value={inputName}
                                onChange={addSetName}
                            />
                            <p>Фамилия</p>
                            <MyInput
                                type='text'
                                placeholder='ваша фамилия'
                                value={inputLastName}
                                onChange={addSetLastName}
                            />
                            <p>Почта</p>
                            <MyInput
                                type='text'
                                placeholder='ваша почта'
                                value={inputMail}
                                onChange={addSetMail}
                            />
                            <p>Пароль</p>
                            <MyInput
                                type='text'
                                placeholder='ваш пороль'
                                value={inputPass}
                                onChange={addSetPass}
                            />

                            <MyButton type="submit" children='зарегистрироватся' />
                        </form>

                    </div>
                </div>
            </div>
        </>
    )
}

export default Registration;
