import React from 'react';
import '../styles/RegistrationStyle.css';
import MyInput from "../components/MyInput";
import MyButton from "../components/MyButton";
import useFormValidation from "../templates/useFormValidation";

const Registration = () => {
    // Define initial state and validators
    const initialState = {
        inputName: '',
        inputLastName: '',
        inputMail: '',
        inputPass: ''
    };

    const validators = {
        inputName: {
            '': 'Name is required',
        },
        inputLastName: {
            '': 'Last name is required',
        },
        inputMail: {
            '': 'Email is required',
            // Add more email validation if needed
        },
        inputPass: {
            '': 'Password is required',
            // Add more password validation if needed
        }
    };

    // Use the custom hook
    const { values, errors, handleChange, handleSubmit } = useFormValidation(initialState, validators);

    const onSubmit = () => {
        // Handle form submission
        console.log('Form submitted:', values);
    };

    return (
        <>
            <div className='main'>
                <div className='container'>
                    <div className='main__content'>
                        <h1>Регистрация</h1>

                        <form onSubmit={handleSubmit(onSubmit)}>
                            <p>Имя</p>
                            <MyInput
                                type='text'
                                name='inputName'
                                placeholder='ваше имя'
                                value={values.inputName}
                                onChange={handleChange}
                            />
                            {errors.inputName && <p>{errors.inputName}</p>}

                            <p>Фамилия</p>
                            <MyInput
                                type='text'
                                name='inputLastName'
                                placeholder='ваша фамилия'
                                value={values.inputLastName}
                                onChange={handleChange}
                            />
                            {errors.inputLastName && <p>{errors.inputLastName}</p>}

                            <p>Почта</p>
                            <MyInput
                                type='text'
                                name='inputMail'
                                placeholder='ваша почта'
                                value={values.inputMail}
                                onChange={handleChange}
                            />
                            {errors.inputMail && <p>{errors.inputMail}</p>}

                            <p>Пароль</p>
                            <MyInput
                                type='password'
                                name='inputPass'
                                placeholder='ваш пороль'
                                value={values.inputPass}
                                onChange={handleChange}
                            />
                            {errors.inputPass && <p>{errors.inputPass}</p>}

                            <MyButton type="submit">Зарегистрироваться</MyButton>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Registration;
