import React from 'react';
import '../styles/RegistrationStyle.css';
import MyInput from "../components/MyInput";
import MyButton from "../components/MyButton";
import { useForm } from "react-hook-form";

const Registration = () => {
    const {
        register,
        handleSubmit,
        reset,
        formState: { errors, isValid },
    } = useForm({
        mode: "onBlur"
    });

    const onSubmit = (data) => {
        alert(JSON.stringify(data));
        console.log(data)
        reset()
    }

    return (
        <>
            <div className='main'>
                <div className='container'>
                    <div className='main__content'>
                        <h1>Регистрация</h1>

                        <form onSubmit={handleSubmit(onSubmit)}>
                            <label>
                                Имя
                                <MyInput {...register('firstName', {
                                        required: 'поле "Имя" обязательно к заполнению',
                                        minLength: {
                                        value: 2,
                                        message: 'минимум 2 символа'
                                    }
                                    })}/>
                                <div className="input-errors">
                                    {errors.firstName && <p>{errors.firstName.message}</p>}
                                </div>
                            </label>

                            <label>
                                Фамилия
                                <MyInput {...register('lastName', {
                                    required: 'поле "Фамилия" обязательно к заполнению',
                                    minLength: {
                                        value: 2,
                                        message: 'минимум 2 символа'
                                    }
                                })}/>
                                <div className="input-errors">
                                    {errors.lastName && <p>{errors.lastName.message}</p>}
                                </div>
                            </label>

                            <label>
                                Email
                                <MyInput {...register('email', {
                                    required: 'поле "Email" обязательно к заполнению',
                                    pattern: {
                                        value: /^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$/,
                                        message: 'аддрес почты должен содержать знаки @ и . '
                                    },
                                })}/>
                                <div className="input-errors">
                                    {errors.email && <p>{errors.email.message}</p>}
                                </div>
                            </label>

                            <label>
                                Пароль
                                <MyInput
                                    {...register('password', {
                                        required: 'поле "Пароль" обязательно к заполнению',
                                        minLength: {
                                            value: 8,
                                            message: 'минимум 8 символов'
                                        },
                                        pattern: {
                                            value: /^(?=.*[a-zA-Z])(?=.*\d)[a-zA-Z\d]+$/,
                                            message: 'пароль должен содержать заглавные буквы, цыфры'
                                        }
                                    })}
                                />
                                <div className="input-errors">
                                    {errors.password && <p>{errors.password.message}</p>}
                                </div>
                            </label>
                            <MyButton type="submit" disable={isValid}>Зарегистрироваться</MyButton>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Registration;
